mapper.select:
SELECT COUNT(*) FROM TBL_ORDER A
JOIN TBL_MEMBER B ON(A.MEMBER_NO = B.MEMBER_NO)
AND A.PAYMETHOD = #{cardPayment, jdbcType=VARCHAR}
AND A.PAYMETHOD = #{kakaoPayment, jdbcType=VARCHAR}
AND A.ORDER_CODE = #{searchValue}
AND B.NAME = #{searchValue}
AND B.PHONE_NUMBER LIKE '%' || #{searchValue} || '%'
AND A.ORDER_STATUS LIKE '%' || #{searchValue} || '%'
AND A.ORDER_DATE >= TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND A.ORDER_DATE <= TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND A.ORDER_DATE > TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') AND A.ORDER_DATE < TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') + 1

SELECT COUNT(*) FROM TBL_REFUND A
JOIN TBL_ORDER B ON(A.ORDER_CODE = B.ORDER_CODE) JOIN TBL_MEMBER C ON(B.MEMBER_NO = C.MEMBER_NO)
AND A.REFUND_CODE = #{searchValue}
AND A.PROCESS_STATUS = #{searchValue}
AND D.NAME = #{searchValue}
AND D.PHONE_NUMBER LIKE '%' || #{searchValue} || '%'
AND A.REFUND_RECEIPT_DATE >= TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND A.REFUND_RECEIPT_DATE <= TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND A.REFUND_RECEIPT_DATE > TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') AND A.REFUND_RECEIPT_DATE < TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') + 1

SELECT A.ORDER_CODE , A.ORDER_DATE , A.ORDER_REQUEST , A.ORDER_PRICE , A.ORDER_DISCOUNT , A.ORDER_TOTAL_PRICE , A.MEMBER_NO , D.NAME , D.PHONE_NUMBER , A.INPUT_ZIP_CODE , A.ORDER_STATUS FROM (SELECT ROWNUM RNUM , B.ORDER_CODE , B.ORDER_DATE , B.ORDER_REQUEST , B.ORDER_PRICE , B.ORDER_DISCOUNT , B.ORDER_TOTAL_PRICE , B.MEMBER_NO , B.INPUT_ZIP_CODE , B.ORDER_STATUS FROM ( SELECT C.ORDER_CODE , C.ORDER_DATE , C.ORDER_REQUEST , C.ORDER_PRICE , C.ORDER_DISCOUNT , C.ORDER_TOTAL_PRICE , C.MEMBER_NO , C.INPUT_ZIP_CODE , C.ORDER_STATUS FROM TBL_ORDER C
JOIN TBL_MEMBER D ON(C.MEMBER_NO = D.MEMBER_NO)
AND C.ORDER_CODE = #{searchValue}
AND D.NAME = #{searchValue}
AND D.PHONE_NUMBER LIKE '%' || #{searchValue} || '%'
AND C.ORDER_STATUS LIKE '%' || #{searchValue} || '%'
AND C.ORDER_DATE >= TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND C.ORDER_DATE <= TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND C.ORDER_DATE > TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') AND C.ORDER_DATE < TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') + 1
ORDER BY C.ORDER_CODE DESC ) B
WHERE ROWNUM <= #{endRow}
) A JOIN TBL_MEMBER D ON (A.MEMBER_NO = D.MEMBER_NO) WHERE A.RNUM >= #{startRow} ORDER BY 1 DESC

SELECT A.ORDER_CODE , A.ORDER_DATE , A.ORDER_TOTAL_PRICE , A.MEMBER_NO , D.NAME , D.PHONE_NUMBER , A.INPUT_ZIP_CODE , A.PAYMETHOD , A.ORDER_STATUS FROM (SELECT ROWNUM RNUM , B.ORDER_CODE , B.ORDER_DATE , B.ORDER_TOTAL_PRICE , B.MEMBER_NO , B.INPUT_ZIP_CODE , B.PAYMETHOD , B.ORDER_STATUS FROM ( SELECT C.ORDER_CODE , C.ORDER_DATE , C.ORDER_TOTAL_PRICE , C.MEMBER_NO , C.INPUT_ZIP_CODE , C.PAYMETHOD , C.ORDER_STATUS FROM TBL_ORDER C
JOIN TBL_MEMBER D ON(C.MEMBER_NO = D.MEMBER_NO)
AND C.PAYMETHOD = #{cardPayment, jdbcType=VARCHAR}
AND C.PAYMETHOD = #{kakaoPayment, jdbcType=VARCHAR}
AND C.ORDER_CODE = #{searchValue}
AND D.NAME = #{searchValue}
AND D.PHONE_NUMBER LIKE '%' || #{searchValue} || '%'
AND C.ORDER_STATUS LIKE '%' || #{searchValue} || '%'
AND C.ORDER_DATE >= TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND C.ORDER_DATE <= TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND C.ORDER_DATE > TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') AND C.ORDER_DATE < TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') + 1
AND C.ORDER_STATUS = '결제완료'
ORDER BY C.ORDER_CODE DESC ) B
WHERE ROWNUM <= #{endRow}
) A JOIN TBL_MEMBER D ON (A.MEMBER_NO = D.MEMBER_NO) WHERE A.RNUM >= #{startRow} ORDER BY 1 DESC

mapper.update:
UPDATE TBL_ORDER
SET ORDER_STATUS = #{newStatus}


        , PROGRESS_DATE = SYSDATE


WHERE ORDER_CODE IN

    #{orderId}

UPDATE TBL_REFUND
SET PROCESS_STATUS = #{newStatus}
,REFUND_COMPLETE_DATE = SYSDATE
WHERE REFUND_CODE IN

    #{orderId}

