SELECT A.REFUND_CODE , A.ORDER_CODE , A.REFUND_RECEIPT_DATE , A.REFUND_COMPLETE_DATE , A.PROCESS_STATUS , A.TOTAL_REFUND_AMOUNT , E.NAME , E.PHONE_NUMBER , D.USE_POINT , D.CP_CODE , A.REFUND_REASON FROM (SELECT ROWNUM RNUM , B.REFUND_CODE , B.ORDER_CODE , B.REFUND_RECEIPT_DATE , B.REFUND_COMPLETE_DATE , B.PROCESS_STATUS , B.TOTAL_REFUND_AMOUNT , B.REFUND_REASON FROM ( SELECT C.REFUND_CODE , C.ORDER_CODE , C.REFUND_RECEIPT_DATE , C.REFUND_COMPLETE_DATE , C.PROCESS_STATUS , C.TOTAL_REFUND_AMOUNT , C.REFUND_REASON FROM TBL_REFUND C
JOIN TBL_ORDER D ON(C.ORDER_CODE = D.ORDER_CODE) JOIN TBL_MEMBER E ON(D.MEMBER_NO = E.MEMBER_NO)
AND C.REFUND_CODE = #{searchValue}
AND C.PROCESS_STATUS = #{searchValue}
AND E.NAME = #{searchValue}
AND E.PHONE_NUMBER LIKE '%' || #{searchValue} || '%'
AND C.REFUND_RECEIPT_DATE >= TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND C.REFUND_RECEIPT_DATE <= TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd')
AND C.REFUND_RECEIPT_DATE > TO_DATE(#{startDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') AND C.REFUND_RECEIPT_DATE < TO_DATE(#{endDate, jdbcType=VARCHAR}, 'yyyy-MM-dd') + 1
ORDER BY C.REFUND_CODE DESC ) B
WHERE ROWNUM <= #{endRow}
) A JOIN TBL_ORDER D ON (A.ORDER_CODE = D.ORDER_CODE) JOIN TBL_MEMBER E ON (D.MEMBER_NO = E.MEMBER_NO) WHERE A.RNUM >= #{startRow} ORDER BY 1 DESC
