mapper.select:
SELECT COUNT(*) FROM TBL_REFUND A
JOIN TBL_ORDER B ON(A.ORDER_CODE = B.ORDER_CODE) JOIN TBL_MEMBER C ON(B.MEMBER_NO = C.MEMBER_NO)
AND A.REFUND_CODE = #{searchValue}
AND A.PROCESS_STATUS = #{searchValue}
AND (C.NAME = #{searchValue} OR C.PHONE_NUMBER LIKE '%' || #{searchValue} || '%')

mapper.update:
UPDATE TBL_ORDER
SET ORDER_STATUS = #{newStatus}
,PROGRESS_DATE = SYSDATE
WHERE ORDER_CODE IN

    #{orderId}

mapper.select:
SELECT A.REFUND_CODE , A.ORDER_CODE , A.REFUND_RECEIPT_DATE , A.REFUND_COMPLETE_DATE , A.PROCESS_STATUS , A.TOTAL_REFUND_AMOUNT , E.NAME , E.PHONE_NUMBER , A.USED_POINT , A.USED_CUPON , A.REFUND_SHIPPING_FEE , A.REFUND_REASON FROM (SELECT ROWNUM RNUM , B.REFUND_CODE , B.ORDER_CODE , B.REFUND_RECEIPT_DATE , B.REFUND_COMPLETE_DATE , B.PROCESS_STATUS , B.TOTAL_REFUND_AMOUNT , B.USED_POINT , B.USED_CUPON , B.REFUND_SHIPPING_FEE , B.REFUND_REASON FROM ( SELECT C.REFUND_CODE , C.ORDER_CODE , C.REFUND_RECEIPT_DATE , C.REFUND_COMPLETE_DATE , C.PROCESS_STATUS , C.TOTAL_REFUND_AMOUNT , C.USED_POINT , C.USED_CUPON , C.REFUND_SHIPPING_FEE , C.REFUND_REASON FROM TBL_REFUND C
JOIN TBL_ORDER D ON(C.ORDER_CODE = D.ORDER_CODE) JOIN TBL_MEMBER E ON(D.MEMBER_NO = E.MEMBER_NO)
AND C.REFUND_CODE = #{searchValue}
AND C.PROCESS_STATUS = #{searchValue}
AND (E.NAME = #{searchValue} OR E.PHONE_NUMBER LIKE '%' || #{searchValue} || '%')
ORDER BY C.REFUND_CODE DESC ) B
WHERE ROWNUM <= #{endRow}
) A JOIN TBL_ORDER D ON (A.ORDER_CODE = D.ORDER_CODE) JOIN TBL_MEMBER E ON (D.MEMBER_NO = E.MEMBER_NO) WHERE A.RNUM >= #{startRow} ORDER BY 1 DESC
